import React from "react";
import "./game.css";
import Die from "./Components/Die";
import nanoid from "nanoid"
const Game = () => {
  // State to store the array of dice values
  const [dice, setDice] = React.useState(allNewDie());
  // Function called when we click the RollButton
  function rollDice() {
    setDice(allNewDie());
  }
  // allNewDie() to create the array with 10 random values
  function allNewDie() {
    const newDice = [];
    for (let i = 0; i < 10; i++) {
        newDice.push({
            value: Math.ceil(Math.random() * 6),
            isHeld: false,
            key:nanoid()
        });
    }

    return newDice;
  }
  // array of <Die /> elements
  const DiceElements = dice.map((die) => <Die value={die} />);

  return (
    <div className="game">
      <div className="dice-container">{DiceElements}</div>
      {/* Dice Button */}
      <button onClick={rollDice} className='roll-dice--button'>Roll Dice</button>
    </div>
  );
};

export default Game;
