import React from 'react'

const Title = () => {
  return (
      <div className='title'>
          <h1 className='title-name'>Tenzies</h1>
          <p className='title-instruction'>
              Roll until all the dice are same . Click at eah die to freeze it at its current value between rolls .
          </p>
    </div>
  )
}

export default Title
